snapshot.javascript:
global.when('start', function () {
  devices.get('mic').onVolumeThreshold(90, true, function (){
    devices.get('camera').flash('#ff9966', 1000);
    devices.get('camera').takePicture();
  });
});

devices.get('camera').when('picture-taken', function () {
  devices.get('camera').addFilter('pixelate', 6);
});

