var ballX, ballY, velX, velY, listX, listY, framenum, dump2;

function listsRepeat(value, n) {
  var array = [];
  for (var i = 0; i < n; i++) {
    array[i] = value;
  }
  return array;
}


global.when('start', function () {
  ballX = 13;
  ballY = 0;
  velX = 0.6;
  velY = 0.4;
  listX = listsRepeat(ballX, 8);
  listY = listsRepeat(ballY, 8);
  framenum = 0;
  time.every(3, 'frames', function () {
    dump2 = listX.pop();
    listX.unshift(Math.floor(ballX));
    dump2 = listY.pop();
    listY.unshift(Math.floor(ballY));
    ballX = ballX + velX;
    ballY = ballY + velY;
    if (ballX > 15) {
      ballX = 15;
      velX = velX * -1;
    }
    if (ballX < 0) {
      ballX = 0;
      velX = velX * -1;
    }
    if (ballY > 8) {
      ballY = 8;
      velY = velY * -1;
    }
    if (ballY < 0) {
      ballY = 0;
      velY = velY * -1;
    }
    devices.get('lightcircle7').setX( Math.floor(ballX));
    devices.get('lightcircle7').setY(Math.floor(ballY));
    devices.get('lightcircle7').setColor('#ff0000');
    devices.get('lightcircle6').setX( listX[1]);
    devices.get('lightcircle6').setY(listY[1]);
    devices.get('lightcircle6').setColor('#ffcc00');
    devices.get('lightcircle5').setX( listX[2]);
    devices.get('lightcircle5').setY(listY[2]);
    devices.get('lightcircle5').setColor('#33ff33');
    devices.get('lightcircle4').setX( listX[3]);
    devices.get('lightcircle4').setY(listY[3]);
    devices.get('lightcircle4').setColor('#33ffff');
    devices.get('lightcircle3').setX( listX[4]);
    devices.get('lightcircle3').setY(listY[4]);
    devices.get('lightcircle3').setColor('#3333ff');
    devices.get('lightcircle2').setX( listX[5]);
    devices.get('lightcircle2').setY(listY[5]);
    devices.get('lightcircle2').setColor('#9999ff');
    devices.get('lightcircle1').setX( listX[6]);
    devices.get('lightcircle1').setY(listY[6]);
    devices.get('lightcircle1').setColor('#ffccff');
    devices.get('lightcircle').setX( listX[7]);
    devices.get('lightcircle').setY(listY[7]);
    devices.get('lightcircle').setColor('#cc33cc');
    velX = velX + Math.sin(framenum / 180 * Math.PI) * 0.2;
    if (Math.floor(ballX) == devices.get('lightrectangle').getX()) {
      if (Math.floor(ballY) == devices.get('lightrectangle').getY()) {
        devices.get('lightrectangle').setX(math.random(0, 15));
        devices.get('lightrectangle').setY(math.random(0, 7));
      }
    }
    devices.get('lightrectangle').setColor('#ffffff');
    if (framenum % 2 == 0) {
      devices.get('lightrectangle').setColor('#cc33cc');
    }
    framenum += 3;
  });
});
