snapshot.javascript:
global.when('start', function () {
  devices.get('camera').onShutterDown(function (){
    devices.get('camera').flash('#ffffff', 200);

  });
  time.every(100, 'milliseconds', function () {
    devices.get('camera').enableKaleidoscope(devices.get('slider1').getValue(), devices.get('slider').getValue(), devices.get('mic').volume / 30);
  });
});

devices.get('button').when('clicked', function () {
  devices.get('camera').takePicture();
});

