function colourRgb(r, g, b) {
  r = Math.max(Math.min(Number(r), 100), 0) * 2.55;
  g = Math.max(Math.min(Number(g), 100), 0) * 2.55;
  b = Math.max(Math.min(Number(b), 100), 0) * 2.55;
  r = ('0' + (Math.round(r) || 0).toString(16)).slice(-2);
  g = ('0' + (Math.round(g) || 0).toString(16)).slice(-2);
  b = ('0' + (Math.round(b) || 0).toString(16)).slice(-2);
  return '#' + r + g + b;
}


global.when('start', function () {
  time.every(20, 'frames', function () {
    devices.get('lightboard').turnOn({"type":"all"}, colourRgb(devices.get('proximitysensor1').getValue() / 2.5, 0, 0));
    if (devices.get('proximitysensor1').getValue() > 200 && devices.get('proximitysensor1').getValue() < 250) {
      speaker.play('perc_snap');
    } else if (devices.get('proximitysensor1').getValue() > 250) {
      speaker.play('perc_bell');
    }
  });
});
