global.when('start', function () {
  time.every(2, 'frames', function () {
    devices.get('lightanimation').play();
    devices.get('lightanimation1').play();
    devices.get('lightanimation2').play();
    devices.get('lightanimation1').goToFrame(0);
    devices.get('lightanimation2').goToFrame(0);
    devices.get('lightanimation').goToFrame(Math.round(devices.get('microphone').volume / 10));
    if (Math.round(devices.get('microphone').volume / 10) > 8) {
      devices.get('lightanimation2').goToFrame(1);
      devices.get('lightanimation1').goToFrame(1);
    }
  });
});

