snapshot.javascript:
devices.get('proximitysensor').when('proximity-update', function () {
  if (devices.get('proximitysensor').getValue() > 220) {
    speaker.play('drum_cymbal_hard');
  } else if (devices.get('proximitysensor').getValue() > 200) {
    speaker.play('bass_hit_c');
  }
});

devices.get('button1').when('clicked', function () {
  speaker.loop('apache');
});

devices.get('button').when('clicked', function () {
  speaker.play('misc_crow');
});

devices.get('button2').when('clicked', function () {
  speaker.stop();
  speaker.play('vinyl_backspin');
});

