var framenum, y, x;


global.when('start', function () {
  x = 0;
  y = 0;
  framenum = 0;
  time.every(3, 'frames', function () {
    for (var i2 = 0; i2 < 16; i2++) {
      for (var i = 0; i < 8; i++) {
        devices.get('lightboard').turnOn({
                            type: 'single',
                            x: x,
                            y: y
                        }, colour.create('hsv', Math.abs((Math.sin((x + framenum) / 180 * Math.PI) * 10 * 10 + Math.cos((y + framenum) / 180 * Math.PI) * 10 * 10) % 255), 100, 100));
        y += 1;
      }
      y = 0;
      x += 1;
    }
    x = 0;
    framenum += 1;
  });
});

framenum;

Math.cos(y / 180 * Math.PI);

