global.when('start', function () {
  devices.get('camera').addFilter('colorize', devices.get('camera').hexToRgb('#ff6600'), 0.3);
  devices.get('camera').onShutterDown(function (){
    devices.get('camera').flash('#ff9966', 1000);
    devices.get('camera').takePicture();

  });
});

devices.get('button1').when('clicked', function () {
  devices.get('camera').flash('#ff9966', 1000);
  devices.get('camera').takePicture();
});

