var wavey, trashcan, counter, height;

function listsRepeat(value, n) {
  var array = [];
  for (var i = 0; i < n; i++) {
    array[i] = value;
  }
  return array;
}

function colourRgb(r, g, b) {
  r = Math.max(Math.min(Number(r), 100), 0) * 2.55;
  g = Math.max(Math.min(Number(g), 100), 0) * 2.55;
  b = Math.max(Math.min(Number(b), 100), 0) * 2.55;
  r = ('0' + (Math.round(r) || 0).toString(16)).slice(-2);
  g = ('0' + (Math.round(g) || 0).toString(16)).slice(-2);
  b = ('0' + (Math.round(b) || 0).toString(16)).slice(-2);
  return '#' + r + g + b;
}


global.when('start', function () {
  wavey = listsRepeat(0, 16);
  time.every(3, 'frames', function () {
    devices.get('lightboard').turnOff({"type":"all"});
    trashcan = wavey.pop();
    wavey.unshift(devices.get('microphone').volume);
    counter = 0;
    for (var i2 = 0; i2 < 17; i2++) {
      height = 8;
      for (var i = 0; i < 8; i++) {
        if (Math.round(wavey[counter - 1] * 0.08) >= height) {
          devices.get('lightboard').turnOn({
                              type: 'single',
                              x: 16 - counter,
                              y: 8 - height
                          }, colourRgb(wavey[counter - 1], 38, 72.5));
        }
        height -= 1;
      }
      counter += 1;
    }
  });
});
