{"parts":[{"id":"tilt1","name":"Tilt 1","type":"gyro-accelerometer","tagName":"kano-part-gyro-accelerometer","userStyle":{},"userProperties":{},"nonvolatileProperties":[],"position":{"x":233,"y":161},"partType":"hardware"}],"code":{"snapshot":{"javascript":"var brushY, brushX;


global.when('start', function () {
  brushX = 8;
  brushY = 4;
  time.every(1, 'frames', function () {
    devices.get('lightboard').turnOn({
                                type: 'single',
                                x: brushX-1,
                                y: brushY-1
                            }, "#ffffff");
    brushX += devices.get('tilt1').getAccelerometerData('x');
    brushY -= devices.get('tilt1').getAccelerometerData('y');
    brushX = Math.min(Math.max(brushX, 1), 16);
    brushY = Math.min(Math.max(brushY, 1), 8);
  });
});
